import React from "react";
import styled, { ThemeProvider } from "styled-components";
import { Grid, Row, Col } from "react-styled-flexboxgrid";

const Box = styled.div`
	background-color: white;
	height: 3rem;
	border: 0.25rem solid red;
`;

const BigBox = styled(Box)`
	height: 7.5rem;
`;

const StyledRow = styled(Row)`
${(props) => {
	if(props.gutter-top) {
		return 'padding-top: ' + props.theme.gutterWidth + 'rem;'
	}
	if(props.gutter-bottom) {
		return 'padding-bottom: ' + props.theme.gutterWidth + 'rem;'
	}
	return 'padding-top: ' + props.theme.gutterWidth + 'rem; padding-bottom: ' + props.theme.gutterWidth + 'rem;'
}}
`

const theme = {
	flexboxgrid: {
		// Defaults
		gridSize: 12, // columns
		gutterWidth: 2, // rem
		outerMargin: 2, // rem
		mediaQuery: "only screen",
		container: {
			sm: 46, // rem
			md: 61, // rem
			lg: 76 // rem
		},
		breakpoints: {
			xs: 0, // em
			sm: 48, // em
			md: 64, // em
			lg: 75 // em
		}
	}
};

const App = () => {
	return (
		<ThemeProvider theme={theme}>
			<Grid fluid>
				<Row>
					<Col xs={2}>
						<Box></Box>
					</Col>
					<Col xs={8}>
						<Box></Box>
					</Col>
					<Col xs={2}>
						<Box></Box>
					</Col>
				</Row>
				<Row>
					<Col xs={4} sm={3} md={2}>
						<Box></Box>
					</Col>
					<Col xs={4} sm={6} md={8}>
						<Box></Box>
					</Col>
					<Col xs={4} sm={3} md={2}>
						<Box></Box>
					</Col>
				</Row>
				<Row>
					<Col xs={4} sm={3} md={2}>
						<Row>
							<Col xs>
								<Box></Box>
							</Col>
						</Row>
						<Row>
							<Col xs>
								<Box></Box>
							</Col>
						</Row>
					</Col>
					<Col xs={4} sm={6} md={8}>
						<BigBox></BigBox>
					</Col>
					<Col xs={4} sm={3} md={2}>
						<BigBox></BigBox>
					</Col>
				</Row>
			</Grid>
		</ThemeProvider>
	);
};

export default App;
